from selenium import webdriver
from selenium.webdriver.chrome.service import Service
from selenium.webdriver.chrome.options import Options

# Set up Chrome options (optional)
chrome_options = Options()
chrome_options.add_argument("--start-maximized")

# Set path to your chromedriver.exe
service = Service('chromedriver.exe')  # Make sure chromedriver.exe is in your PATH or specify the full path

# Initialize the WebDriver
driver = webdriver.Chrome(service=service, options=chrome_options)

# Access the URL
driver.get("https://odibets.com/odileague")

# Optional: Wait for a few seconds to see the page
import time
time.sleep(5)

# Close the browser
driver.quit()